wp_revslider_navigations.css:
{"arrows":".custom.tparrows {
	background-color: #fff;
    width: 110px;
    height: 100px;
}
.custom.tparrows:before {
    color: #7e7e7e;
    line-height: 100px;
    font-size: 24px;
    font-family: \'Font Awesome 5 Pro\';
    font-weight: 300;
}
.custom.tp-leftarrow:before {
	text-align: right;
    padding-right: 25px;
    content: \'\\f104\';
}
.custom.tp-rightarrow:before {
	text-align: left;
    padding-left: 25px;
    content: \'\\f105\';
}
.custom.tparrows:hover:before,
.custom.tparrows:focus:before,
.custom.tparrows:active:before {
	color: #e4573d;
}
@media (max-width: 991px) {
	.custom.tparrows {
    	height: 60px;
    }
    .custom.tparrows:before {
        line-height: 60px;
    }
}
"}
